@Component({
  selector: '.app-servers' /* '[app-servers]', */,
  /* template: `<app-server></app-server> <app-server></app-server>`, */
  templateUrl: './servers.component.html',
  styleUrls: ['./servers.component.css'],
})
export class ServersComponent {
  allowNewServer = false;

  constructor() {
    setTimeout(() => {
      this.allowNewServer;
    }, 20000);
  }
}
