import { Component } from '@angular/core';

@Component({
  selector: 'app-success-alert',
  template: `<p>This is the Success Alert component!</p>`,
  styles: [
    `
      p {
        padding: 20px;
        background-color: forestgren;
        border: 1px solid green;
      }
    `,
  ],
})
export class SuccessAlertComponent {}
